# Tools

You may call one or more functions to assist with the user query.

You are provided with function signatures within <tools></tools> XML tags:
<tools>
{"type": "function", "function": {"name": "create_component", "description": "Create a new component in Sketchup", "parameters": {"properties": {"type": {"default": "cube", "description": "", "title": "Type", "type": "string"}, "position": {"description": "", "items": {}, "title": "Position", "type": "array"}, "dimensions": {"description": "", "items": {}, "title": "Dimensions", "type": "array"}}, "title": "create_component_Args", "type": "object", "additionalProperties": false, "required": ["type", "position", "dimensions"]}}}
{"type": "function", "function": {"name": "delete_component", "description": "Delete a component by ID", "parameters": {"properties": {"id": {"description": "", "title": "Id", "type": "string"}}, "required": ["id"], "title": "delete_component_Args", "type": "object", "additionalProperties": false}}}
{"type": "function", "function": {"name": "transform_component", "description": "Transform a component's position, rotation, or scale", "parameters": {"properties": {"id": {"description": "", "title": "Id", "type": "string"}, "position": {"description": "", "items": {}, "title": "Position", "type": "array"}, "rotation": {"description": "", "items": {}, "title": "Rotation", "type": "array"}, "scale": {"description": "", "items": {}, "title": "Scale", "type": "array"}}, "required": ["id", "position", "rotation", "scale"], "title": "transform_component_Args", "type": "object", "additionalProperties": false}}}
{"type": "function", "function": {"name": "get_selection", "description": "Get currently selected components", "parameters": {"properties": {}, "title": "get_selection_Args", "type": "object", "additionalProperties": false, "required": []}}}
{"type": "function", "function": {"name": "set_material", "description": "Set material for a component", "parameters": {"properties": {"id": {"description": "", "title": "Id", "type": "string"}, "material": {"description": "", "title": "Material", "type": "string"}}, "required": ["id", "material"], "title": "set_material_Args", "type": "object", "additionalProperties": false}}}
{"type": "function", "function": {"name": "export_scene", "description": "Export the current scene", "parameters": {"properties": {"format": {"default": "skp", "description": "", "title": "Format", "type": "string"}}, "title": "export_scene_Args", "type": "object", "additionalProperties": false, "required": ["format"]}}}
{"type": "function", "function": {"name": "create_mortise_tenon", "description": "Create a mortise and tenon joint between two components", "parameters": {"properties": {"mortise_id": {"description": "", "title": "Mortise Id", "type": "string"}, "tenon_id": {"description": "", "title": "Tenon Id", "type": "string"}, "width": {"default": 1, "description": "", "title": "Width", "type": "number"}, "height": {"default": 1, "description": "", "title": "Height", "type": "number"}, "depth": {"default": 1, "description": "", "title": "Depth", "type": "number"}, "offset_x": {"default": 0, "description": "", "title": "Offset X", "type": "number"}, "offset_y": {"default": 0, "description": "", "title": "Offset Y", "type": "number"}, "offset_z": {"default": 0, "description": "", "title": "Offset Z", "type": "number"}}, "required": ["mortise_id", "tenon_id", "width", "height", "depth", "offset_x", "offset_y", "offset_z"], "title": "create_mortise_tenon_Args", "type": "object", "additionalProperties": false}}}
